mapper.select:
select d.version_id as senceVersionId,d.title as senceName,a.RULE_NAME as ruleName,a.RULE_DESC as ruleDesc,sum(1) as flag from RISK_RULE_HIS_VERSION a inner join rule_scene_version d on a.SENCE_VERSION_ID = d.version_id inner join RISK_TEST_DROOLS_LOG b on a.SENCE_VERSION_ID = b.SENCE_VERSIONID and d.version_id = b.SENCE_VERSIONID inner join RISK_TEST_DROOLS_DETAIL_LOG c on a.RULE_NAME = c.EXECUTE_RULENAME and b.ID = c.DROOLS_LOGID where b.PROCINST_ID in
#{procInstId}
group by d.version_id,d.title,a.RULE_DESC,a.RULE_NAME;
