mapper.select:
SELECT DISTINCT T.RULE_ID, T.SCENE_ID, T.RULE_NAME, T.RULE_DESC, T.RULE_ENABLED, T.IS_EFFECT, T.CRE_USER_ID, T.CRE_TIME, T.REMARK FROM RULE_INFO T WHERE 1 = 1
AND T.SCENE_ID = #{sceneId}
AND T.RULE_ENABLED = #{ruleEnabled}

SELECT DISTINCT T.RULE_PROPERTY_ID, T.RULE_PROPERTY_IDENTIFY, T.RULE_PROPERTY_NAME, T.RULE_PROPERTY_DESC, T.DEFAULT_VALUE, T.IS_EFFECT, T.REMARK FROM RULE_PROPERTY_INFO T WHERE 1 = 1
AND T.RULE_PROPERTY_IDENTIFY = #{rulePropertyIdentify}
AND T.RULE_PROPERTY_NAME LIKE CONCAT('%',#{rulePropertyName},'%')

SELECT DISTINCT R.RULE_ID, R.SCENE_ID, R.RULE_NAME, R.RULE_DESC, R.RULE_ENABLED, R.IS_EFFECT, R.CRE_USER_ID, R.CRE_TIME, R.REMARK FROM RULE_INFO R INNER JOIN RULE_SCENE_INFO S ON S.SCENE_ID = R.SCENE_ID WHERE 1 = 1 AND S.IS_EFFECT = 1 AND R.IS_EFFECT = 1 AND R.RULE_ENABLED = 1
AND S.SCENE_IDENTIFY = #{sceneIdentify}
AND S.SCENE_ID = #{sceneId}
