mapper.select:
select SENCE_VERSIONID as senceVersionId ,AVG(execute_time) executeTime ,count(execute_time) execTotal  from RISK_DROOLS_LOG t  where 1=1

    and CREATE_TIME>=#{startTime}


    and CREATE_TIME  <=  #{endTime}

GROUP BY SENCE_VERSIONID  order by  execute_time desc

    limit 0,#{getWay}

select rd.EXECUTE_RULENAME executeRuleName,count(DROOLS_LOGID) executeTotal from RISK_TEST_DROOLS_LOG rl inner join RISK_TEST_DROOLS_DETAIL_LOG rd on  rl.ID=rd.DROOLS_LOGID
where  1=1

    and rl.CREATE_TIME>=#{startTime}


    and rl.CREATE_TIME  <=  #{endTime}

GROUP BY  EXECUTE_RULENAME

    limit 0,#{getWay}
